// Setup functions
async function initializeTestDirectories() {
  const testDirs = [
    'tests/integration/temp',
    'tests/integration/fixtures',
    'tests/integration/output'
  ];
  
  for (const dir of testDirs) {
    if (!fs.existsSync(dir)) {
      fs.mkdirSync(dir, { recursive: true });
    }
  }
}

